import { Injectable } from '@angular/core';
import { Actions, Effect, ofType } from '@ngrx/effects';
import { Action, Store } from '@ngrx/store';
import { catchError, exhaustMap, map, withLatestFrom } from 'rxjs/operators';
import { I18n } from '../../shared/model/model';
import { GlobalState } from '../../shared/state';
import { I18nApiActions } from '../actions';
import { I18nService } from '../services/i18n.service';

@Injectable()
export class I18nApiEffects {
  constructor(
    private actions$: Actions,
    private i18nService: I18nService,
    private store: Store<GlobalState>) {
  }

  @Effect()
  getI18n = this.actions$.pipe(
    ofType(I18nApiActions.getI18n),
    withLatestFrom(this.store),
    exhaustMap(([action, state]: [Action , GlobalState]) => this.i18nService.getI18n(state.appState.language).pipe(
      map((i18n: I18n) => I18nApiActions.getI18nSuccess({i18n})),
      catchError((err: Error) => I18nApiActions.getI18nFailure)
    ))
  );
}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { environment } from '../../../environments/environment';
import { mapI18nApiToI18nClient } from '../../shared/helpers/helpers';
import { AvailableLanguages, I18n as I18nClient } from '../../shared/model/model';
import { I18n as I18nApi } from '../../shared/model/model-api';

@Injectable({
  providedIn: 'root'
})
export class I18nService {
  constructor(private httpClient: HttpClient) {
  }

  getI18n(lang: AvailableLanguages): Observable<I18nClient> {
    return this.httpClient.get<I18nApi>(`${environment.apiUrl}/i18n/${lang}`).pipe(
      map((i18n: I18nApi) => mapI18nApiToI18nClient(i18n))
    );
  }
}

