  }

  @Effect()
  loadRecipes = this.actions$.pipe(
    ofType(RecipeApiActions.loadRecipes, CookbookApiActions.loadCookbookSuccess, RecipeApiActions.deleteRecipeFailure),
    withLatestFrom(this.store.select(((state: GlobalState) => state.cookbookState.activeCookbookId))),
    switchMap(([_, activeCookbookId]) => this.recipeService.loadRecipes(activeCookbookId)
      .pipe(
        map((recipes: Recipe[]) => RecipeApiActions.loadRecipesSuccess({recipes})),
        catchError(() => of(RecipeApiActions.loadRecipesFailure()))
      )
    )
  );

  @Effect()
  saveRecipe = this.actions$.pipe(
    ofType(RecipeApiActions.createRecipe),
    withLatestFrom(this.store.select(((state: GlobalState) => state.cookbookState.activeCookbookId))),
    switchMap(([action, activeCookbookId]) => this.recipeService.saveRecipe(activeCookbookId, action.recipeToSave)
      .pipe(
        map((recipe: Recipe) => RecipeApiActions.createRecipeSuccess({savedRecipe: recipe})),
        catchError(() => of(RecipeApiActions.createRecipeFailure()))
      )
    )
  );

  @Effect({dispatch: false})
  navigateToCookbook = this.actions$.pipe(
    ofType(RecipeApiActions.createRecipeSuccess, RecipeApiActions.editRecipeSuccess),
    tap(() => this.router.navigate(['/cookbook']))
  );

  @Effect()
  editRecipe = this.actions$.pipe(
    ofType(RecipeApiActions.editRecipe),
    withLatestFrom(this.store.select(((state: GlobalState) => state.cookbookState.activeCookbookId))),
    switchMap(([action, cookbookId]) => this.recipeService.editRecipe(cookbookId, action.recipeToEdit)
      .pipe(
        map((recipe: Recipe) => RecipeApiActions.editRecipeSuccess({editedRecipe: recipe})),
        catchError(() => of(RecipeApiActions.editRecipeFailure()))
      )
    )
  );

  @Effect()
  deleteRecipe = this.actions$.pipe(
    ofType(RecipeApiActions.deleteRecipe),
    switchMap(({recipeId}: { recipeId: string }) => this.recipeService.deleteRecipe(recipeId)
      .pipe(
        map(() => RecipeApiActions.deleteRecipeSuccess({recipeId})),
        catchError(() => of(RecipeApiActions.deleteRecipeFailure()))
      )
    )
  );
}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { environment } from '../../../environments/environment';
import { convertRecipeApiToRecipe, convertRecipesApiToRecipes } from '../../shared/helpers/helpers';
import { Recipe } from '../../shared/model/model';
import { RecipeApi } from '../../shared/model/model-api';

@Injectable()
export class RecipeService {
  constructor(private httpClient: HttpClient) {
  }

  loadRecipes(cookBookId: string): Observable<Recipe[]> {
    return this.httpClient.get<RecipeApi[]>(`${environment.apiUrl}/recipes?cookbook=${cookBookId}`).pipe(
      map((recipes: RecipeApi[]) => convertRecipesApiToRecipes(recipes))
    );
  }

  saveRecipe(cookBookId: string, recipe: Recipe): Observable<Recipe> {
    return this.httpClient.post<RecipeApi>(`${environment.apiUrl}/recipes`, {...recipe, cookbook: cookBookId}).pipe(
      map((savedRecipe: RecipeApi) => convertRecipeApiToRecipe(savedRecipe))
    );
  }

  editRecipe(cookBookId: string, recipe: Recipe): Observable<Recipe> {
    return this.httpClient.put<RecipeApi>(`${environment.apiUrl}/recipes/${recipe.id}`, {...recipe, cookbook: cookBookId}).pipe(
      map((editedRecipe: RecipeApi) => convertRecipeApiToRecipe(editedRecipe))
    );
  }

  deleteRecipe(recipeId: string): Observable<string>{
    return this.httpClient.delete(`${environment.apiUrl}/recipes/${recipeId}`, {
      responseType: 'text'
    });
  }
}

import { Injectable } from '@angular/core';
import { MatSnackBar, MatSnackBarConfig, MatSnackBarRef } from '@angular/material/snack-bar';
import { TextOnlySnackBar } from '@angular/material/snack-bar/simple-snack-bar';
import { Store } from '@ngrx/store';
import { TranslatePipe } from '../../i18n/pipes/translate.pipe';
import { I18n, Language } from '../model/model';
import { GlobalState, selectTranslations } from '../state';

@Injectable({
  providedIn: 'root'
})
export class SnackbarService {

  private translations: I18n | null | undefined;
  private currentLang: Language | undefined;

  constructor(
    private snackBar: MatSnackBar,
    private store: Store<GlobalState>,
    private translatePipe: TranslatePipe
  ) {
    this.store.select(selectTranslations).subscribe((translations: I18n | null) => this.translations = translations);
    this.store.select((state: GlobalState) => state.appState.language).subscribe((lang: Language) => this.currentLang = lang);
  }

  openSnackBar(messageKey: string, actionKey: string, config?: MatSnackBarConfig): MatSnackBarRef<TextOnlySnackBar> {
    const message = this.translatePipe.transform(messageKey, this.translations || null, this.currentLang);
    const action = this.translatePipe.transform(actionKey, this.translations || null, this.currentLang);
    return this.snackBar.open(message, action, config || {
      duration: 10000
    });
  }
}

