      },
      {
        value: 'pack',
        key: this.translatePipe.transform('forms.units.pack', this.translations, this.currentLang)
      },
      {
        value: 'piece',
        key: this.translatePipe.transform('forms.units.piece', this.translations, this.currentLang)
      },
      {value: 'kg', key: 'kg'},
      {value: 'g', key: 'g'},
      {value: 'l', key: 'l'},
      {value: 'dl', key: 'dl'},
      {value: 'ml', key: 'ml'},
    ];
  }


}

