import { Component, EventEmitter, forwardRef, Input, OnInit, Output } from '@angular/core';
import { ControlValueAccessor, FormControl, NG_VALUE_ACCESSOR } from '@angular/forms';
import { SelectOption } from '../../model/model';
import { v4 as uuid } from 'uuid';



@Component({
  selector: 'app-select',
  templateUrl: './select.component.html',
  styleUrls: ['./select.component.scss'],
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => SelectComponent),
      multi: true
    }
  ]})
export class SelectComponent<T> implements OnInit, ControlValueAccessor {
  @Input() control: FormControl | undefined;
  @Input() name = '';
  @Input() options: SelectOption<T>[] = [];
  @Input() label: string | undefined;
  @Input() errors: string[] = [];
  @Input() isDisabled = false;
  @Input() e2eTestName = '';
  @Output() valueChanged: EventEmitter<T | string> = new EventEmitter();

  selectedValue: T | string | null = null;
  inputId: string = uuid();

  constructor() { }

  ngOnInit(): void {
    this.selectedValue = this.options[0].value;
    this.propagateChange(this.options[0].value);
  }

  onChange(option: T | string): void {
    this.propagateChange(option);
  }

  getOptionKey(option: SelectOption<T>): string {
    return option?.key ?? ((typeof option.value === 'string') ? option.value : JSON.stringify(option.value));
  }

  propagateChange = (value: T | string) => {};

  registerOnChange(fn: () => {}): void {
    this.propagateChange = fn;
  }

  registerOnTouched(fn: () => {}): void {
  }

  writeValue(value: T | string): void {
    if (value !== undefined) {
      this.selectedValue = value;
    }
  }
}

import { Component, EventEmitter, Input, Output } from '@angular/core';
import { ArrayItemMovedEvent, Language, I18n, ShoppingListItem } from '../../../shared/model/model';
import { DEFAULT_LANGUAGE } from '../../../shared/helpers/constants';

@Component({
  selector: 'app-shopping-list',
  templateUrl: './shopping-list.component.html',
  styleUrls: ['./shopping-list.component.scss']
})
export class ShoppingListComponent{
  @Input() items: ShoppingListItem[] = [];
  @Input() translations: I18n = {};
  @Input() currentLang: Language = DEFAULT_LANGUAGE;
  @Output() itemDeleted: EventEmitter<ShoppingListItem> = new EventEmitter();
  @Output() listItemMoved: EventEmitter<ArrayItemMovedEvent> = new EventEmitter();

  deleteBuffer: {[key: string]: number} = {};
  deletionDelayInMilliseconds = 1500;


  itemChecked(item: ShoppingListItem, isChecked: boolean): void {
    item.isChecked = isChecked;

    if (isChecked) {
      this.deleteBuffer[item.id] = window.setTimeout(() => this.itemDeleted.emit(item), this.deletionDelayInMilliseconds);
    } else {
      clearTimeout(this.deleteBuffer[item.id]);
    }
  }

  drop({previousIndex, currentIndex}: ArrayItemMovedEvent): void {
    this.listItemMoved.emit({currentIndex, previousIndex});
  }
}

