        ...state.recipes.filter((item: Recipe) => item.id !== editedRecipe.id),
        editedRecipe
      ]
    };
  }),
  on(RecipeStateActions.deleteRecipeFromState, (state: RecipeState, {recipeId}: { recipeId: string }) => {
    return {
      ...state,
      recipes: state.recipes.filter(item => item.id !== recipeId)
    };
  })
)
