import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Store } from '@ngrx/store';
import { Observable } from 'rxjs';
import { take } from 'rxjs/operators';
import { v4 as uuid } from 'uuid';
import { STORAGE_SELECTED_COOKBOOK_ID } from '../../../../core/constants/constants';
import { I18n, Language, Recipe } from '../../../../core/models/model';
import { StorageService } from '../../../../core/services/storage.service';
import { GlobalState, selectActiveCookbookId, selectedRecipe, selectTranslations } from '../../../../core/store';
import { RecipeContainerActions } from '../../store/actions';

@Component({
  selector: 'app-recipe-container',
  templateUrl: './recipe-container.component.html',
  styleUrls: ['./recipe-container.component.scss']
})
export class RecipeContainerComponent implements OnInit {

  translations$: Observable<I18n | null> = this.store.select(selectTranslations);
  currentLanguage$: Observable<Language | null> = this.store.select((state: GlobalState) => state.appState.language);
  id: string | null = '';
  recipe$: Observable<Recipe | undefined> | undefined;

  constructor(
    private store: Store<GlobalState>,
    private router: Router,
    private route: ActivatedRoute,
    private storageService: StorageService,
  ) {
  }

  ngOnInit(): void {
    const selectedCookbookId = this.storageService.getItem(STORAGE_SELECTED_COOKBOOK_ID);
    this.id = this.route.snapshot.paramMap.get('id');
    if (this.id) {
      this.store.dispatch(RecipeContainerActions.loadRecipe({id: this.id!}));
      this.recipe$ = this.store.select(selectedRecipe(selectedCookbookId, this.id));
    }
  }

  onRecipeSaved(recipe: Recipe): void {
    !!this.id ?
      this.store.dispatch(RecipeContainerActions.editRecipe({recipeToEdit: recipe})) :
      this.store.select(selectActiveCookbookId).pipe(
        take(1)
      ).subscribe((activeCookbookId: string) => this.store.dispatch(RecipeContainerActions.createRecipe({
          optimisticId: uuid(),
          recipeToSave: {
            ...recipe,
            cookbookId: activeCookbookId
          }
        }))
      );
  }

}

import { Component, ElementRef, EventEmitter, Input, OnDestroy, OnInit, Output, ViewChild } from '@angular/core';
import { fromEvent, Subject } from 'rxjs';
import { debounceTime, distinctUntilChanged, map, takeUntil } from 'rxjs/operators';
import { DEFAULT_LANGUAGE, INPUT_DEBOUNCE_TIME } from '../../../../core/constants/constants';
import { I18n, Language, Recipe } from '../../../../core/models/model';

@Component({
  selector: 'app-recipe-list',
  templateUrl: './recipe-list.component.html',
  styleUrls: ['./recipe-list.component.scss']
})
export class RecipeListComponent implements OnInit, OnDestroy {

  @Input() translations: I18n | null = {};
  @Input() currentLang: Language | null = DEFAULT_LANGUAGE;
  @Input() recipes: Recipe[] | undefined | null;
  @Input() isOffline: boolean | null = false;
  @Output() inputChanged: EventEmitter<string> = new EventEmitter<string>();
  @Output() editRecipe: EventEmitter<string> = new EventEmitter<string>();
  @Output() deleteRecipe: EventEmitter<Recipe> = new EventEmitter<Recipe>();
  @Output() clickRecipe: EventEmitter<Recipe> = new EventEmitter<Recipe>();

  @ViewChild('searchField', {static: true, read: ElementRef}) searchField: ElementRef | undefined;

  destroy$: Subject<void> = new Subject();

  constructor() {
  }

  ngOnInit(): void {
    fromEvent<InputEvent>(this.searchField?.nativeElement, 'input').pipe(
      takeUntil(this.destroy$),
      debounceTime(INPUT_DEBOUNCE_TIME),
      distinctUntilChanged(),
      map((event: InputEvent) => (event?.target as HTMLInputElement).value)
    ).subscribe((value: string) => this.inputChanged.emit(value));
  }

  onEditRecipe(id: string | undefined): void {
    if (id) {
      this.editRecipe.emit(id);
    }
  }

  onDeleteRecipe(recipe: Recipe): void {
      this.deleteRecipe.emit(recipe);
  }

  onClick(recipe: Recipe): void {
    this.clickRecipe.emit(recipe);
  }

  getPageState(items: Recipe[] | null | undefined): string {
    if (items === null || items === undefined) {
      return 'loading';
    }

    if (items.length === 0) {
      return 'empty';
    }

    return 'default';
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }
}

