import { Pipe, PipeTransform } from '@angular/core';
import { Language, I18n } from '../../shared/model/model';
import { DEFAULT_LANGUAGE } from '../../shared/helpers/constants';


@Pipe({name: 'translate'})
export class TranslatePipe implements PipeTransform {
  transform(key: string, i18n: I18n | null, language?: Language | null): string {
    return (i18n && i18n[language || DEFAULT_LANGUAGE] && i18n[language || DEFAULT_LANGUAGE][key]) ?? key;
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { ShoppingListItem } from '../../shared/model/model';

@Pipe({name: 'unToggled'})
export class UnToggledPipe implements PipeTransform {
  transform(value: ShoppingListItem[] | null): ShoppingListItem[] {
    if (!value) {
      return [];
    }
    return value.filter(shoppingListItem => !shoppingListItem.isChecked);
  }
}

