import { Injectable } from '@angular/core';
import { Actions, Effect, ofType } from '@ngrx/effects';
import { Store } from '@ngrx/store';
import { GlobalState, selectRequestedUrlBeforeLoginWasRequired } from '../../shared/state';
import { AuthApiActions, LoginPageActions, LoginServiceActions } from '../actions';
import { catchError, exhaustMap, map, tap, withLatestFrom } from 'rxjs/operators';
import { AuthService } from '../services/auth.service';
import { LoginAction } from '../../shared/model/model-action';
import { JwtRefreshResponse, User } from '../../shared/model/model';
import { of } from 'rxjs';
import { AppInitializationActions, ErrorInterceptorActions, NavActions } from '../../shared/state/app-actions';
import { Router } from '@angular/router';
import { DEFAULT_REDIRECT_URL_FOR_LOGGED_IN_USER } from '../../shared/helpers/constants';

@Injectable()
export class AuthApiEffects {
  constructor(
    private actions$: Actions,
    private authService: AuthService,
    private router: Router,
    private store: Store<GlobalState>) {
  }

  @Effect()
  login = this.actions$.pipe(
    ofType(LoginPageActions.login),
    exhaustMap(({credentials}: LoginAction) => this.authService.login(credentials).pipe(
      map((user: User) => AuthApiActions.loginSuccess({user})),
      catchError(() => of(AuthApiActions.loginFailure()))
    )),
  );

  @Effect()
  refreshToken = this.actions$.pipe(
    ofType(AppInitializationActions.refreshToken, LoginServiceActions.refreshToken),
    exhaustMap(() => this.authService.refreshToken().pipe(
      map((jwtRenewal: JwtRefreshResponse) => {
        return (jwtRenewal.ok && jwtRenewal.user) ?
          AuthApiActions.refreshTokenSuccess({user: jwtRenewal.user}) : AuthApiActions.refreshTokenFailed();
      }),
      catchError(() => of(AuthApiActions.refreshTokenFailed()))
    )),
  );

  @Effect()
  logout = this.actions$.pipe(
    ofType(ErrorInterceptorActions.logout, NavActions.logout),
    exhaustMap(() => this.authService.logout().pipe(
      map(() => AuthApiActions.logoutSuccess()),
      catchError(() => of(AuthApiActions.loginFailure()))
    )),
  );

  @Effect({dispatch: false})
  redirectWhenLoggedIn = this.actions$.pipe(
    ofType(AuthApiActions.loginSuccess),
    withLatestFrom(this.store.select(selectRequestedUrlBeforeLoginWasRequired)),
    tap(([_, url]) => {
      this.router.navigate([url || DEFAULT_REDIRECT_URL_FOR_LOGGED_IN_USER]);
    }),
  );

  @Effect({dispatch: false})
  redirectWhenLoggedOut = this.actions$.pipe(
    ofType(AuthApiActions.logoutSuccess),
    withLatestFrom(this.store.select(selectRequestedUrlBeforeLoginWasRequired)),
    tap(([_, url]) => {
      this.router.navigate(['/']);
    }),
  );
}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map, tap } from 'rxjs/operators';
import { environment } from '../../../environments/environment';
import { decodeJwtToken, mapUserApiToUserClient } from '../../shared/helpers/helpers';
import { UserApi } from '../../shared/model/model-api';
import { JwtRefreshResponse, LoginCredentials, User } from '../../shared/model/model';
import { GlobalState } from '../../shared/state';
import { Store } from '@ngrx/store';
import { LoginServiceActions } from '../actions';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private refreshTokenTimeout: number | undefined;

  constructor(private httpClient: HttpClient, private store: Store<GlobalState>) {
  }

  login(credentials: LoginCredentials): Observable<User> {
    return this.httpClient.post<UserApi>(`${environment.authUrl}/auth/local`, credentials).pipe(
      map((userApi: UserApi) => mapUserApiToUserClient(userApi)),
      tap(user => this.startRefreshTokenTimer(user.jwt))
    );
  }

  refreshToken(): Observable<JwtRefreshResponse> {
    return this.httpClient.post<JwtRefreshResponse>(`${environment.authUrl}/auth/refresh-token`, {}).pipe(
      tap((jwtRenewal: JwtRefreshResponse) => jwtRenewal.user && this.startRefreshTokenTimer(jwtRenewal.user.jwt))
    );
  }

  logout(): Observable<true> {
    this.stopRefreshTokenTimer();
    return this.httpClient.post<true>(`${environment.authUrl}/auth/logout`, {});
  }

  private startRefreshTokenTimer(jwt: string): void {
    const {exp} = decodeJwtToken(jwt);

    const expires = new Date(exp * 1000);
    // set a timeout to refresh the token a minute before it expires
    const timeout = expires.getTime() - Date.now() - (60 * 1000);
    this.stopRefreshTokenTimer();
    this.refreshTokenTimeout = window.setTimeout(() => this.store.dispatch(LoginServiceActions.refreshToken()), timeout);
  }

  private stopRefreshTokenTimer(): void {
    clearTimeout(this.refreshTokenTimeout);
  }
}

